select project, subscriptionguid, subscriptionname, "cost code" ,"cost centre", sum(cost) as cost  FROM kpmgcostanalysisathenadatabase.azure left JOIN kpmgcostanalysisathenadatabase.accounts ON azure.subscriptionguid = accounts.account_number where month = CAST((month(now())-1) AS VARCHAR) group by  project, account_number, subscriptionguid, subscriptionname, "cost code", "cost centre"